package Controle;

public class For2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int cont;
for(cont=0;cont<=20;cont++) {
	System.out.println(cont);
}
int x;
for(x=0;x<10;x++) {
	System.out.println(x);
}
int y;
for(y=2;y<50;y+=2) {
	System.out.println(y);
}
int a;
for(a=5;a<25;a+=5) {
	System.out.println(a);
}
}
	}


